import React from "react";

class GradeFilesUploadPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null,
      grades: [],
      selected_grade: null
    };
  }

  componentDidMount() {
    fetch("http://localhost:8080/grade")
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          this.setState({
            grades: data.results
          });
        }
      })
      .catch(error => {
        console.log(error);
      });
  }

  handleRadioButton = e => {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  };

  onSubmit = async evt => {
    evt.preventDefault(); // stop the page from refreshing
    const { selected_grade, file } = this.state;
    if (!selected_grade) {
      alert("Please select a grade");
    } else if (!file) {
      alert("Please select a file");
    } else {
      const body = new FormData();
      body.append("file", file);
      body.append("grade_id", selected_grade);
      fetch("http://localhost:8080/file", { method: "POST", body })
        .then(response => {
          return response.json();
        })
        .then(answer => {
          if (answer.success) {
            this.fileInput.value = "";
            this.setState({
              file: null,
              selected_grade: null
            });
            alert("file uploaded sucessfully ");
          }
        })
        .catch(error => {
          debugger;
          alert("file was not uploaded. Please upload it using zip file !");
        });
    }
  };

  render() {
    return (
      <div>
        <form onSubmit={this.onSubmit}>
          <div className="container">
            <div className="row">
              <div className="col">
                <h1>Select a Grade</h1>

                <div className="grade-wrapper">
                  <select onChange={this.handleRadioButton} name='selected_grade'>
                    {this.state.grades.map((grade, index) => {
                      return <option value={grade.id}>{grade.name}</option>;
                    })}
                  </select>
                </div>
                <div className="row">
                  <div className="col">
                    <input
                      type="file"
                      name="file"
                      ref={ref => (this.fileInput = ref)}
                      onChange={e => {
                        this.setState({
                          file: e.target.files[0]
                        });
                      }}
                    />
                    {/* <input type="submit" value="Upload Photo" onClick={}/>  */}
                    <button type="submit"> Submit</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </form>
      </div>
    );
  }
}

export default GradeFilesUploadPage;
